sources.sass\\main.scss:
/*
 * Главный файл стилей.
 * Все паршалы подключаются сюда.
 */

@import './variables';
@import './mixins';
@import './visually-hidden';
@import './dropp';
@import './visually-hidden';
@import './container';
@import './subtitle';
@import './title';
@import './buttons';
@import './page-header';
@import './page-hero';
@import './products';
@import './about';
@import './advantages';
@import './gallery';
@import './reviews';
@import './contacts';
@import './footer';
@import './about-modal';
@import './buyNowModals';

sources.sass\\_variables.scss:
$main-font-family: 'DM Sans', sans-serif;
$title-font-family: 'Titan One', cursive;

/*** COLOR TEXT ****/
$color-main: #ffffff;
$color-secondary: #d41443;
$color-pink: #ffa5ba;
$color-dark: #000000;
$color-light-dark: #907e82;

/** BACKGROUND COLOR **/
$bg-main: #ffffff;
$bg-light-dark: #e5e5e5;
$bg-light-pink: #fff5f6;
$bg-secondary: #d41342;
$bg-green: #c2e297;
$bg-gold: #f0d1a5;
$bg-hero: #ffb8ca;
$bg-ice-cream: #ffa5ba;
$bg-mobile-menu: #e17992;

sources.sass\\_visually-hidden.scss:
.visually-hidden {
    position: absolute !important;
    clip: rect(1px 1px 1px 1px); /* IE6, IE7 */
    clip: rect(1px, 1px, 1px, 1px);
    padding: 0 !important;
    border: 0 !important;
    height: 1px !important;
    width: 1px !important;
    overflow: hidden;
  }

sources.sass\\_dropp.scss:
*,
::after,
::before {
  box-sizing: border-box;
  outline: none;
}
html{
  scroll-behavior: smooth;
}
body {
  font-family: $main-font-family;
  font-style: normal;
  margin: 0;
  // background-color: $bg-hero;
}

h1,
h2,
h3,
h4,
h5,
h6,
p {
  margin: 0;
}
img {
  display: block;
  height: auto;
}

a {
  text-decoration: none;
  cursor: pointer;
}

Ul,
ol {
  list-style: none;
  margin: 0;
  padding: 0;
}

sources.sass\\_container.scss:
.container {
  padding: 0 20px;
  width: 100%;

  .gallery & {
    padding: 0;
  }

  .footer &{
    padding-bottom: 43px;
  }
  
  @media screen and (min-width: 480px) {
    margin: 0 auto;
    width: 480px;
  }
  @media screen and (min-width: 768px) {
    width: 768px;

    .footer &{
      display: flex;
      padding-bottom: 91px;
    }
  }
  @media screen and (min-width: 1200px) {
    width: 1200px;

    .footer &{
      padding-bottom: 97px;
    }
  }
}

sources.sass\\_subtitle.scss:
.subtitle {
  font-family: $title-font-family;
  font-weight: 400;
  font-size: 18px;
  line-height: 1.167;
  text-transform: uppercase;
  color: $color-pink;
  text-align: center;
  letter-spacing: 0.04em;
  margin-bottom: 15px;

  @media screen and (min-width: 480px) {
    font-size: 20px;
  }
  @media screen and (min-width: 768px) {
    font-size: 26px;
    line-height: 1.154;
  }
  @media screen and (min-width: 1200px) {
    font-size: 30px;
    line-height: 1.133;
  }
}

sources.sass\\_title.scss:
.title {
  font-family: $title-font-family;
  font-weight: 400;
  font-size: 34px;
  line-height: 1.147;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #d41443;
  @media screen and (min-width: 480px) {
    font-size: 38px;
  }
  @media screen and (min-width: 768px) {
    font-size: 48px;
    line-height: 1.146;
  }
  @media screen and (min-width: 1200px) {
    font-size: 58px;
    line-height: 1.138;
    letter-spacing: 0.06em;
  }
}

sources.sass\\_buttons.scss:
.main-button {
  font-weight: 700;
  font-size: 16px;
  line-height: 1.312;
  text-align: center;
  font-family: inherit;
  border-radius: 22px;
  color: $color-secondary;
  background-color: $bg-main;
  border-color: transparent;
  min-width: 80px;
  height: 44px;
  padding: 10px;
  cursor: pointer;
  transition: all 300ms linear;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover,
  &:focus {
    background-color: $bg-secondary;
    color: $color-main;
  }
  @media screen and (min-width: 768px) {
    min-width: 160px;
  }
}
.main-button-red {
  background-color: $bg-secondary;
  color: $color-main;

  &:hover,
  &:focus {
    color: $color-secondary;
    background-color: $bg-main;
  }
}

.hero-button {
  font-family: inherit;
  font-weight: 400;
  font-size: 8px;
  line-height: 1.25;
  text-align: center;
  height: 24px;
  min-width: 62px;
  border-radius: 20px;
  border-color: transparent;
  padding: 5px 10px;
  background-color: $bg-main;
  color: $color-secondary;
  cursor: pointer;
  transition: all 300ms linear;
  margin-right: 8px;

  &:hover,
  &:focus {
    background-color: $bg-secondary;
    color: $color-main;
  }

  @media screen and (min-width: 1200px) {
    height: 40px;
    font-size: 14px;
    line-height: 1.286;
    min-width: 104px;
    margin-right: 15px;
  }
}
.hero-button-red {
  background-color: $bg-secondary;
  color: $color-main;

  &:hover,
  &:focus {
    background-color: $bg-main;
    color: $color-secondary;
  }
}

.close-icon {
  width: 14px;
  height: 14px;
}

.burger-icon {
  width: 20px;
  height: 12px;
}

sources.sass\\_page-header.scss:
.link-logo {
  padding: 5px 0;
  display: block;
  @media screen and (min-width: 1200px) {
    margin-right: 110px;
  }
}
.page-header {
  background-color: $bg-hero;
  @media screen and (min-width: 768px) {
    padding-top: 36px;
  }
}

.page-header .container {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.navigation {
  display: flex;
  
}

.nav-list-desktop {
  display: none;
  
  @media screen and (min-width: 1200px) {
    display: flex;
    z-index: 25;
  }
}


.nav-item-desktop {
  display: flex;
  align-items: center;
  

 
  
}

.nav-link-desktop {
  transition: all 300ms linear;
  display: flex;
  align-items: center;
  font-family: $main-font-family;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.312;
  
  color: #FFFFFF;

  &:last-child {
    margin-right: 35px;
  } 
 

  &.active {
    color: $color-secondary;
  }

  &:hover,
  &:focus {
    color:$color-secondary;
  }
  
  
}
.btn-burger {
  width: 40px;
  height: 40px;
  padding: 10px;
  background-color: transparent;
  border: none;
  display: flex;
  align-items: center;

  @media screen and (max-width: 1199px) {
    z-index: 99;
  }
  @media screen and (min-width: 1200px) {
    display: none;
  } 
    
  }


.mobile-menu-container {
  position: fixed;
  top: 0;
  right: 0;
  background-color: $bg-mobile-menu;
  height: 100vh;
  width: 100%;
  padding: 64px 0;
  z-index: 150;
  transform: translateX(100%);
  transition: all 500ms linear;
  &.is-open{
    transform: translateX(0);
  }
}

.mobile-menu-container .main-button {
  margin: 0 auto;
  width: 186px;
  margin-bottom: 20px;
}
.navigation-item {
  border-top: 1px solid #e18298;
  &:last-child {
    border-bottom: 1px solid #e18298;
  }
}
.navigation-link {
  font-weight: 700;
  font-size: 14px;
  line-height: 1.286;

  color: $color-main;
  padding: 12px 24px;
  display: block;
  &:hover,
  &:focus {
    color: $color-secondary;
  }

  @media screen and (min-width: 480px) {
    font-size: 24px;
  }
}
.active {
    color: $color-secondary;
  }
.menu-mobile-close {
  width: 18px;
  height: 18px;
  background-color: transparent;
  border: none;
  border-radius: 50%;
  position: absolute;
  top: 20px;
  right: 70px;
  @media screen and (min-width:768px) {
    top: 30px;
     right: 150px;    
  }
}
.arrow-btn {
  margin-left: 18px;
  padding-top: 4px;
}
.header-button {
  display: none;
  @media screen and (min-width: 768px) {
    display: block;
    z-index: 99;
  }
}

.buttons-container {
  display: flex;
  align-items: center;
  justify-content: center;
}

sources.sass\\_page-hero.scss:
.page-hero {
  padding-top: 120px;
  padding-bottom: 160px;
  width: 100%;
  margin: 0 auto;
  position: relative;
  overflow: hidden;
  background-color: $bg-hero;
}
.page-hero .container {
  display: flex;
}

.hero-title {
  font-family: $title-font-family;
  font-weight: 400;
  font-size: 26px;
  line-height: 1.154;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  width: 220px;
  margin-bottom: 20px;
  color: $color-main;
}
.hero-title-accent {
  color: $color-secondary;
  font-size: 44px;
}
.hero-left-side {
  position: relative;
  z-index: 24;
}
.circle {
  position: absolute;
  width: 350px;
  height: 350px;
  bottom: -90px;
  left: -195px;

  border-radius: 50%;
  background: #ffa5ba;
  transition: box-shadow 500ms ease-out;
  &:hover{
  box-shadow: -3px 3px 30px 5px rgba(206,138,172,0.54);
-webkit-box-shadow: -3px 3px 30px 5px rgba(206,138,172,0.54);
-moz-box-shadow: -3px 3px 30px 5px rgba(206,138,172,0.54);
  }
}
.hero-right-side {
  position: relative;
}
.hero-img {
  position: absolute;
  z-index: 55;
  left: -120px;
  bottom: -160px;
  @media screen and (max-width:767px) {
    width: 171px;
  height: 410px;
  }
}
.hero-description{
  display: none;

  &-img{
    display: block;
  }
}

.hero-description_right-side{
  & .button-products{
    width: 18px;
    height: 18px;
    justify-self: flex-start;
    align-self: start;
  margin: 0;
  }
  & .right-arrow{
    font-size: 8px;
  }
  display: flex;
  flex-direction: column;
}

.milk-description{
  margin-bottom: 10px;
  display: block;
  width: 114px;
  height: 48px;
  font-family: DM Sans;
font-style: normal;
font-weight: normal;
font-size: 8px;
line-height: 150%;
color: $color-main;
line-height: 12px;
}
.hero-rs-second{
  display: none
}
@media screen and (min-width:768px){
  .item-places {
    text-align: right;
    white-space: pre-wrap;
  }

  .hero-places-number{
    font-family: "Titan One", cursive;
    font-weight: 400;
    font-size: 28px;
    line-height: 1.154;
    letter-spacing: 0.04em;
    text-transform: uppercase;
    margin-bottom: 2px;
    color: $color-secondary;
    position: relative;
    z-index: 55;
    &::before{
      position: absolute;
      display: block;
      content: " ";
      border-radius: 50%;
      width: 30px;
      height: 30px;
      top: -12px;
    right: -19px;
      background-color: #fff;
      z-index: -1;
    }
  }

  .hero-tasty-img{
    position: absolute;
    right: 0px;
    bottom: -28px;
    width: 168px;
    transition: transform 300ms linear;
  }

  .hero-rs-second{
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    position: relative;
  }

  .page-hero .container{
    justify-content: space-between;
  }

.page-hero{
  padding-bottom: 28px;
  padding-top: 15px;
  overflow: visible;
}

.hero-title {
  font-size: 22px;
  width: 162px;
}

.hero-title-accent{
  font-size: 34px;
}

.circle{
     left: -130px;
    bottom: 8px;
  width: 324px;
  height: 322px;
}
  .hero-img{
    left: -55px;
    bottom: -28px;
  }

  .hero-button{
    margin-bottom: 20px;
  }

  .hero-description{
    display: flex;
    align-items: flex-end;
  }
}

@media screen and (min-width:1200px){
  .circle{
    width: 538px;
    height: 538px;
    left: -260px;
    bottom: 56px;
  }
  .hero-img{
    left: -135px;
  }
  .hero-rs-second{
    margin-top: -50px;
  }
  .padding-left{
    padding-left: 50px;
    margin-bottom: 56px;
  }
  .hero-description-img{
    margin-right: 30px;
    width: 188px;
    height: 191px;
  }
  .hero-description_right-side .button-products {
    width: 26px;
    height: 26px;
  }

  .hero-description_right-side .right-arrow{
    font-size: 12px;
  }
  .hero-description{
    align-items: center;
  }
  .milk-description{
    font-size: 14px;
    width: 200px;
    line-height: 20px;
    min-height: 78px;
  }
  .hero-places-number::before{
    top: 0px;
    right: -15px;
  }
  .hero-places-number{
    font-size: 46px;
    line-height: 53px;
  }

  .list-places .milk-description {
    font-size: 14px;
  }
  .item-places:not(:last-child){
    margin-bottom: 30px;
  }
  .hero-title{
    font-size: 38px;
    width: 277px;
  }

  .hero-title-accent{
    font-size: 58px;
  }
  .page-hero{
    padding-top: 74px;
  }

   .hero-tasty-img{
     width: 293px;
   }
}

sources.sass\\_products.scss:
.products {
  padding-top: 120px;
  padding-bottom: 98px;
  background-color: $bg-main;

  @media screen and (min-width: 768px) {
    padding-top: 122px;
    padding-bottom: 112px;
  }

  @media screen and (min-width: 1200px) {
    padding-top: 126px;
    padding-bottom: 120px;
  }
}

.products-list {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  @media screen and (min-width: 768px) {
    flex-direction: row;
    align-items: baseline;
  }
}

.products-item {
  display: flex;
  align-items: center;
  flex-direction: column;
  padding: 160px 40px 41px 40px;
  position: relative;
  border-radius: 24px;

  .overlay {
    width: 100%;
    overflow: hidden;
    position: relative;
  }

  .overlay-text-pink,
  .overlay-text-gold,
  .overlay-text-green {
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    transform: translateX(-100%);
    transition: all 250ms cubic-bezier(0.4, 0, 0.2, 1);
    font-family: $main-font-family;
    font-weight: 700;
    font-size: 14px;
    line-height: 1.5;
    text-align: center;
    letter-spacing: 0.04em;
    color: $bg-main;

    @media screen and (min-width: 768px) {
      line-height: 1.76;
      overflow: hidden;
    }

    @media screen and (min-width: 1200px) {
      font-size: 16px;
    }
  }

  @media screen and (min-width: 768px) {
    padding: 152px 11px 40px 11px;
    // width: 220px;
    padding-bottom: 40px;

    &:not(:last-child) {
      margin-right: 20px;
    }
  }

  @media screen and (min-width: 1200px) {
    // width: 329px;
    // height: 525px;
    padding: 163px 44px 50px 44px;

    &:not(:last-child) {
      margin-right: 29px;
    }
  }

  &:not(:last-child) {
    margin-bottom: 130px;

    @media screen and (min-width: 768px) {
      margin-bottom: 0;
    }
  }

  &.pink {
    background-color: $bg-hero;
  }

  &.gold {
    background-color: $bg-gold;
  }

  &.green {
    background-color: $bg-green;
  }
}
.green:hover {
  box-shadow: -4px 4px 43px 0px rgba(7, 150, 20, 0.75);
  -webkit-box-shadow: -4px 4px 43px 0px rgba(7, 150, 20, 0.75);
  -moz-box-shadow: -4px 4px 43px 0px rgba(7, 150, 20, 0.75);
}
.gold:hover {
  box-shadow: -4px 4px 43px 0px rgba(158, 158, 16, 0.75);
  -webkit-box-shadow: -4px 4px 43px 0px rgba(158, 158, 16, 0.75);
  -moz-box-shadow: -4px 4px 43px 0px rgba(158, 158, 16, 0.75);
}
.pink:hover {
  box-shadow: -4px 4px 43px 0px rgba(244, 112, 195, 0.75);
  -webkit-box-shadow: -4px 4px 43px 0px rgba(244, 112, 195, 0.75);
  -moz-box-shadow: -4px 4px 43px 0px rgba(244, 112, 195, 0.75);
}

.products-item-name {
  font-family: $title-font-family;
