      background-image: url('../images/images/products/milk-shakes/milkshakesDecktop@2x.png');
    }
  }
}

.products-item-point {
  position: relative;
  margin-left: auto;
  margin-right: auto;
  margin-top: 30px;
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background-color: $bg-main;

  @media screen and (min-width: 1200px) {
    margin-top: 32px;
  }
}

.products-item-point::before {
  position: absolute;
  left: 10px;
  content: '';
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background-color: $bg-main;
}

.products-item-point::after {
  position: absolute;
  left: -10px;
  content: '';
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background-color: $bg-main;
}

.products-item-but {
  margin-top: 30px;
}

.products .title {
  margin-bottom: 144px;

  @media screen and (min-width: 768px) {
    margin-bottom: 150px;
  }

  @media screen and (min-width: 1200px) {
    margin-bottom: 259px;
  }
}

.products-item-text {
  width: 200px;
  height: 124px;
  margin-top: 30px;
  margin-bottom: 30px;
  font-family: $main-font-family;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.5;
  text-align: center;
  letter-spacing: 0.04em;
  color: $bg-main;

  @media screen and (min-width: 768px) {
    margin-bottom: 40px;
    line-height: 1.76;
    width: 198px;
    overflow: hidden;
  }

  @media screen and (min-width: 1200px) {
    margin-bottom: 62px;
    margin-top: 32px;
    font-size: 16px;
    height: 106px;
    width: 241px;
  }
}

.button-products {
  margin: 0 auto;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  outline: none;
  border: none;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  background-color: $bg-main;
  transition: all 300ms linear;

  &:hover .overlay-text-pink,
  &:focus .overlay-text-pink {
    transform: translateX(0);
    background: $bg-hero;
    padding: 0;
    animation: fadeInLeftBig 1000ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  &:hover .overlay-text-gold,
  &:focus .overlay-text-gold {
    transform: translateX(0);
    background: $bg-gold;
    padding: 0;
    animation: fadeInLeftBig 1000ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  &:hover .overlay-text-green,
  &:focus .overlay-text-green {
    transform: translateX(0);
    background: $bg-green;
    padding: 0;
    animation: fadeInLeftBig 1000ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  &:hover,
  &:focus {
    background-color: $bg-secondary;
    color: $color-main;
  }

  &:hover .right-arrow,
  &:focus .right-arrow {
    color: $color-main;
  }
}

.right-arrow {
  transition: all 300ms linear;
  color: $color-secondary;
}

