sources.sass\\_reviews.scss:
.reviews {
  padding-top: 80px;
  // padding-bottom: 86px;
  background-color: $bg-main;
  @media screen and (min-width: 768px) {
    & {
      padding: 120px 0;
    }
  }
  @media screen and (min-width: 1200px) {
    & {
      padding-top: 123px;
      padding-bottom: 132px;
    }
  }
}

.reviews-list-item {
  margin-bottom: 30px;
}

.reviews-photo-div {
  margin: 0 auto;
  margin-bottom: 30px;
  width: 85px;
  height: 85px;
  border-radius: 50%;
}

.review-div {
  position: relative;
  margin: 0 auto;
  margin-bottom: 30px;
  min-width: 280px;
  background-color: #fafafa;
  padding: 43px 50px 20px;
  &::before {
    content: '';
    display: block;
    position: absolute;
    left: 61px;
    top: 28px;
    width: 54px;
    height: 40px;
    background-image: url('../images/images/gallery/post/“.svg');
  }
}

.review {
  position: relative;
  padding: 0px 16px 0px 7px;
  font-weight: 500;
  font-size: 12px;
  line-height: 2.55;
  text-align: center;
  color: $color-dark;
  background-image: repeating-linear-gradient(
    to top,
    #e1e1e1,
    #e1e1e1 1px,
    transparent 1px,
    transparent 31px
  );
  background-repeat: no-repeat;
}

.pseudoelement {
  position: relative;
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background-color: #c7133e;
  margin: 0 auto;
  margin-bottom: 18px;
}

.pseudoelement::before {
  position: absolute;
  display: block;
  top: 0;
  left: -14px;
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background-color: #c7133e;
  content: '';
}

.pseudoelement::after {
  position: absolute;
  display: block;
  top: 0;
  right: -14px;
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background-color: #c7133e;
  content: '';
}

.review-author {
  font-weight: 500;
  font-size: 14px;
  line-height: 1.86;
  text-align: center;
}

@media screen and (min-width: 768px) {
  .review-div {
    padding: 47px 36px 28px 60px;
    width: 620px;
    &::before {
      top: 30px;
      left: 40px;
    }
  }

  .review {
    padding: 0px 44px 0px 20px;
    font-size: 14px;
    line-height: 2.16;
    letter-spacing: 0.04em;
    background-image: repeating-linear-gradient(
      to top,
      #e1e1e1,
      #e1e1e1 1px,
      transparent 1px,
      transparent 30px
    );
  }

  .review-author {
    font-size: 16px;
  }
}

@media screen and (min-width: 1200px) {
  .review-div {
    padding: 47px 54px 32px 59px;
    width: 926px;
    &::before {
      top: 32px;
      left: 50px;
    }
  }

  .review {
    padding: 0px 35px 0px 28px;
    font-size: 16px;
    line-height: 1.86;
  }

  .review-author {
    font-size: 20px;
  }
}

.slider {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.slick-prev {
  opacity: 0;
  display: none;
}

.slick-next {
  display: block;

  border: none;
  outline: none;
  width: 12.4px;
  height: 12px;
  font-size: 0;
  background-image: url(../images/Vector.png);
  background-repeat: no-repeat;
  background-size: contain;
  background-color: transparent;
}

sources.sass\\_contacts.scss:
.contacts {
  padding-top: 86px;
  padding-bottom: 113px;
  background: linear-gradient(to bottom, $bg-main 11%, $bg-light-pink 11% 89%);
}

.contacts-list {
  margin-bottom: 54px;
}

.contacts-item {
  width: 220px;
  padding: 43px 20px;
  background-color: $bg-main;
  box-shadow: 0px 8px 30px rgba(212, 20, 67, 0.1);
  border-radius: 24px;

  @media screen and (max-width: 767px) {
    margin: auto;
    &:not(:last-child) {
      margin-bottom: 20px;
    }
  }
}

.contacts-representation {
  margin-bottom: 25px;
  text-transform: uppercase;
  color: $color-main;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.833;
  text-align: center;
}

.rectangle-cafe {
  width: 80px;
  height: 19px;
  background: $bg-gold;
  border-radius: 4px;
}

.rectangle-foodtruck {
  width: 130px;
  height: 19px;
  background: $bg-green;
  border-radius: 4px;
}

.contacts-wrap {
  &::after {
    content: '';
    display: block;
    width: 100%;
    height: 1px;
    background-color: #e1e1e1;
    margin-top: 25px;
    margin-bottom: 25px;
  }
}

.contacts-wrap-days {
  margin-bottom: 10px;
}

.contacts-subtitle {
  margin-bottom: 10px;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.833;
  color: $color-dark;
}

.contacts-desc,
.contact-link {
  font-weight: 500;
  font-size: 12px;
  line-height: 1.5;
  color: $color-light-dark;
}

.contact-link {
  cursor: pointer;
}

.contacts-days {
  font-weight: 500;
  font-size: 12px;
  line-height: 1.833;
  color: $color-dark;
}
.contacts-time {
  color: $color-pink;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.183;
}

.contacts-btn-list {
  text-align: center;
}

//buttons//
.contacts-btn-list {
  display: flex;
  justify-content: center;
  align-items: center;

  @media screen and (max-width: 767px) {
    flex-direction: column;
  }
}

@media screen and (max-width: 767px) {
  .contacts-btn-item {
    &:not(:last-child) {
      margin-bottom: 10px;
    }
  }
  .contacts-btn-red,
  .contacts-btn-white {
    width: 161px;
  }
}

//backdrop

.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  z-index: 100;
  background-color: rgba(0, 0, 0, 0.2);
  transition: 300ms linear;
}

.is-hidden {
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
}

.contacts-modal {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 90%;
  height: 85%;
  padding: 30px;
  border-radius: 28px;
  box-shadow: 0px 1px 3px rgb(0 0 0 / 12%), 0px 1px 1px rgb(0 0 0 / 14%),
    0px 2px 1px rgb(0 0 0 / 20%);
  background-color: $bg-mobile-menu;
  overflow-y: scroll;

  @media screen and (min-width: 480px) {
    max-width: 440px;
    overflow-y: hidden;
  }
}

.contacts-map-img {
  width: 100%;
}

.contacts-map {
  font-size: 20px;
  font-weight: 500;
  display: block;
  margin-bottom: 10px;
  text-align: center;
  color: black;
  cursor: pointer;
  transition: color 250ms linear;

  &:hover,
  &:focus {
    color: $bg-secondary;
  }
}

.contacts-btn-close {
  position: absolute;
  top: 13px;
  right: 13px;
  width: 25px;
  height: 25px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 4px;
  background-color: rgba(0, 0, 0, 0.3);
  border: 1px solid white;
  border-radius: 50%;
  cursor: pointer;
  transition: box-shadow 300ms linear;
  transition: background-color 300ms linear;

  &:hover,
  &:focus {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.2), 0px 4px 4px rgba(0, 0, 0, 0.1),
      1px 4px 6px rgba(0, 0, 0, 0.2);
    background-color: $bg-secondary;
  }
}

.contacts-modal-2 {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 90%;
  height: 85%;
  background-color: $bg-mobile-menu;
  background-size: contain;
  background-repeat: no-repeat;
  overflow-y: scroll;
  box-shadow: 0px 1px 3px rgb(0 0 0 / 12%), 0px 1px 1px rgb(0 0 0 / 14%),
    0px 2px 1px rgb(0 0 0 / 20%);
  border-radius: 28px;

  @media screen and (min-width: 480px) {
    max-width: 440px;
  }
}
.contacts-modal-form {
  padding: 60px;
}
.contacts-modal-label {
  display: flex;
  flex-direction: column;
  margin-bottom: 25px;
  font-size: 13px;
  color: $color-dark;
}

.contacts-modal-input {
  width: 100%;
  height: 40px;
  padding: 8px;
  background-color: $bg-light-pink;
  font-size: 16px;
  border: none;
  border-radius: 15px;
  padding-left: 15px;
  font-size: 13px;
