      }
}

.footer-contacts-tel {
  display: inline-block;
  color: $color-secondary;
  @include font(12px, 700, 22px, 0.02em);
  text-align: right;

  @media screen and (min-width: 768px) {
    @include font(14px, 700, 26px, 0.02em);
    }

    @media screen and (min-width: 1200px) {
      @include font(16px, 700, 30px, 0.02em);
      }
}

.footer-rights {
  border-top: 1px solid #e1e1e1;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.footer-rights-thumb {
  margin-top: 22px;
  margin-bottom: 22px;
  width: 150px;
  display: block;

  @media screen and (min-width: 768px) {
    width: 100%;
    margin-top: 29px;
    margin-bottom: 18px;
  }

  @media screen and (min-width: 1200px) {
    margin-top: 9px;
    margin-bottom: 10px;
  }
}

.footer-rights-text {
  font-family: $main-font-family;
  text-align: center;
  @include font(14px, 700, 21px, 0.02em);
  color: $color-light-dark;
}

.footer-thumb{
  @media screen and (min-width: 768px) {
    margin-left: auto;
    }
}

.footer-title-thumb {
  max-width: 232px;

  @media screen and (min-width: 768px) {
    max-width: 330px;
    margin-right: 92px;
    }

    @media screen and (min-width: 1200px) {
      max-width: 480px;
      }
}

.footer-main-title {
  font-family: $title-font-family;
  text-transform: uppercase;
  color: $bg-ice-cream;
  @include font(18px, 400, 21px, 0.02em);

  @media screen and (min-width: 768px) {
    @include font(26px, 400, 30px, 0.04em);
    }
    @media screen and (min-width: 1200px) {
      @include font(30px, 400, 34px, 0.04em);
      }

}

.footer-secondary-title {
  font-family: $title-font-family;
  text-transform: uppercase;
  color: $color-secondary;
  @include font(34px, 400, 39px, 0.04em);
  margin-bottom: 17px;

  @media screen and (min-width: 768px) {
    @include font(48px, 400, 55px, 0.06em);
    margin-bottom: 16px;
    }

    @media screen and (min-width: 1200px) {
      @include font(58px, 400, 66px, 0.06em);
      margin-top: 15px;
      }  
}

sources.sass\\_about-modal.scss:
.about-backdrop {
  position: fixed;
  z-index: 100;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.2);
  transition: all 400ms linear;
}
.about-modal {
  position: absolute;
  width: 90%;
  height: 90%;
  left: 50%;
  top: 50%;
  max-width: 450px;
  padding: 20px;
  max-width: 450px;
  background-image: url('../images/images/about/bio-eco/biomilkDecktop@1x.png');
  background-repeat: no-repeat;
  background-size: contain;
  transform: translate(-50%, -50%);
  background-color: #f0e6e6;
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 28px;
}
.modal-close-btn {
  position: absolute;
  width: 35px;
  height: 35px;
  top: 15px;
  right: 15px;
  background: transparent;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 50%;
  cursor: pointer;
  &:hover {
    background-color: tomato;
    // fill: blue;
  }
}
.about-icon-close {
  margin-left: -3px;
  margin-top: 1px;
  width: 28px;
  height: 28px;
  fill: red;
  &:hover {
    fill: rgb(255, 255, 255);
  }
}
.is-hidden {
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
}
.modal-title {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 20px;
  line-height: 34px;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  margin-top: 20px;
  color: #ec8da3;
}
.about-img-modal {
  width: 470px;
  margin-top: -30px;
  margin-left: -30px;
  position: absolute;
  filter: alpha(Opacity=50);
  opacity: 0.2;
}
.about-bg-modal {
  max-width: 100%;
  filter: alpha(Opacity=50);
  opacity: 0.5;
}
.modal-text {
  font-family: DM Sans;
  font-style: normal;
  font-weight: 500;
  font-size: 15px;
  line-height: 1.94;
  letter-spacing: 0.04em;

  color: #907e82;
  text-align: center;
  max-width: 100%;
  margin-top: 120px;
}

sources.sass\\_buyNowModals.scss:
.backdrop-buy {
  position: fixed;
  z-index: 100;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.2);
  transition: all 400ms linear;
}
.is-hidden {
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
}
.modal-buy-now {
  position: absolute;
  left: 50%;
  top: 50%;
  width: 90%;
  height: 90%;
  max-width: 450px;
  padding: 20px;
  z-index: 999999;

  transform: translate(-50%, -50%);
  background-color: #f0e6e6;
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 28px;
}
.calculator-form {
  background-color: #f0e6e6;
  padding: 30px 25px;
}

.calculator-form-title {
  font-weight: bold;
  font-size: 17px;
  text-decoration: uppercase;
  line-height: 135%;
  color: $color-secondary;
  margin-bottom: 15px;
}

.calculator-form-input {
  display: flex;
  flex-direction: column;
  margin-bottom: 30px;
}

.calculator-form-select {
  background: transparent;
  border: none;
  border-bottom: 1px solid $bg-gold;
  padding: 5px 0 17px 0;
  outline: none;
}

.calculator-form-input-field {
  margin-right: 10px;
}

.calculator-form-input-radio-container {
  display: flex;
  align-items: center;
}

.calculator-form-input-label {
  margin-right: 15px;
  font-weight: normal;
  font-size: 17px;
  line-height: 150%;
  color: $color-dark;
}

.calculator-form-total-cost {
  font-weight: bold;
  font-size: 36px;
  line-height: 150%;
  color: $color-dark;
  margin: 0;
}

.calculator-form-total {
  font-weight: bold;
  font-size: 17px;
  line-height: 135%;
  color: $color-secondary;
  margin: 0;
}
.calculator-form-input .tech-input-container .choices__item {
  background-color: $color-pink;
  border: none;
  border-radius: 3px;
  position: relative;
}

.calculator-form-input .tech-input-container:after {
  content: '\f107';
  height: 0;
  width: 0;
  position: absolute;
  right: 10px;
  top: 45%;
  margin-top: -2.5px;
  cursor: pointer;
  font-family: inherit;
  font-weight: 900;
}

.calculator-form-input .tech-input-container {
  background: transparent;
  border-radius: 0;
  border: none;
  border-bottom: 1px solid $bg-gold;
  padding-left: 0;
  display: flex;
  align-items: center;
  min-height: 35px;
  box-sizing: border-box;
}

.calculator-form-input .choices__input {
  display: none;
}

.calculator-form-input .choices__item .choices__button {
  border-left: none;
  margin: 0;
  padding-left: 8px;
}
